mapper.select:
		SELECT  * FROM (   
		SELECT   ROW_NUMBER() OVER(ORDER BY QSTN_CODE DESC)
		 NUM, A.*,M.MEMBER_NAME FROM QUESTION A 
         JOIN MEMBERS M
         ON A.MEMBER_CODE = M.MEMBER_CODE
         ORDER BY QSTN_CODE)  
		WHERE NUM BETWEEN #{ cri.rowStart }
		AND #{ cri.rowEnd } 
		AND MEMBER_CODE = #{ memberCode }
		ORDER BY QSTN_CODE DESC
